.main-body {
  color: #000000;
  background-color: #ffffff;
}

.container {
  max-width: 1160px;
  padding: 0;
  margin-left: auto;
  margin-right: auto;
}

.btn {
  -webkit-box-sizing: border-box;
          box-sizing: border-box;
  display: inline-block;
  font-size: 15px;
  line-height: 48px;
  padding: 0 20px;
  cursor: pointer;
  border: none;
  outline: none;
  background-color: inherit;
  -webkit-transition: 0.2s all ease;
  -o-transition: 0.2s all ease;
  transition: 0.2s all ease;
}

.main-header {
  position: relative;
  background-color: #f7e296;
  margin-top: 40px;
  padding-top: 30px;
  padding-left: 60px;
  padding-right: 60px;
  min-height: 300px;
}

.main-header h1 {
    margin: 0;
    padding: 0;
    font-family: "Gilroyextrabold", sans-serif;
    font-size: 48px;
    line-height: 48px;
    position: absolute;
    top: -24px;
  }

.main-header .top-nav__list {
    margin: 0;
    padding: 0;
    list-style-type: none;
    font-size: 0;
  }

.main-header .top-nav__list li a {
      font-size: 15px;
      line-height: 36px;
      font-family: "OpenSans-Light", sans-erif;
      color: #000000;
    }

.main-header .top-nav__list li {
      cursor: pointer;
      display: inline-block;
      padding: 0;
      font-size: 0;
      -webkit-transition: 0.2s all ease;
      -o-transition: 0.2s all ease;
      transition: 0.2s all ease;
    }

.main-header .top-nav__list .list-search {
      position: relative;
      width: 440px;
      margin-right: 100px;
      background: url("e1785ea3971b9615ce8cd4908a6a1b3c.png") no-repeat left center;
    }

.main-header .top-nav__list .list-search input {
        position: relative;
        height: 48px;
        opacity: 0.3;
        font-size: 15px;
        line-height: 36px;
        font-family: "OpenSans-Light", sans-erif;
        color: #000000;
        border: none;
        outline: none;
        padding: 0;
        background-color: inherit;
        cursor: pointer;
        width: 100%;
        padding-left: 30px;
      }

.main-header .top-nav__list .list-search input:hover {
          opacity: 0.6;
        }

.main-header .top-nav__list .list-search input:focus {
          opacity: 1;
          border-bottom: 2px solid #000000;
        }

.main-header .top-nav__list span {
      font-size: 15px;
      line-height: 48px;
      position: absolute;
      display: block;
      top: -2px;
      right: 0;
      border: 2px solid #000000;
      border-bottom: none;
      display: none;
      -webkit-transition: 0.2s all ease;
      -o-transition: 0.2s all ease;
      transition: 0.2s all ease;
    }

.main-header .top-nav__list input:focus + span {
      display: block;
    }

.main-header .top-nav__list span:hover {
      color: #ffffff;
      background-color: #000000;
    }

.main-header .top-nav__list span .btn:active {
      opacity: 0.3;
    }

.main-header .top-nav__list .list-enter {
      padding-left: 30px;
      width: 260px;
      margin-right: 20px;
      background: url("f98bbddee83fb4a4470fcae2d954e17e.png") no-repeat left center;
    }

.main-header .top-nav__list .list-compare {
      padding-left: 30px;
      width: 110px;
      background: url("d1d7474dd8c8f042e50e04655604b589.png") no-repeat left center;
    }

.main-header .top-nav__list .list-basket {
      width: 85px;
      padding-left: 25px;
      background: url("88e8ec650f8481148d54a183a7295f72.png") no-repeat left center;
    }

.top-nav {
  margin-bottom: 35px;
}

.middle-nav {
  margin-bottom: 90px;
}

.middle-nav-list {
  font-size: 0;
  margin: 0;
  padding: 0;
  list-style-type: none;
}

.middle-nav-list li {
    display: inline-block;
  }

.middle-nav-list li a {
      text-transform: uppercase;
      font-size: 18px;
      line-height: 24px;
      color: #000000;
      font-family: "Gilroyextrabold", sans-serif;
      padding-bottom: 28px;
    }

.middle-nav-list .middle-nav-list__catalog {
