.wrap-catalog-menu {
  position: absolute;
  top: 40px;
  width: 1040px;
  height: 147px;
  background-color: #f7e296;
  visibility: hidden;
}

.catalog-menu {
  font-size: 0;
  margin: 0;
  padding: 0;
  width: 570px;
}

.catalog-menu li {
    display: inline-block;
  }

